import machinefrom machine import Pinimport timefrom ui import Rotaryfrom FSM import Fsmrotary = Rotary(3,2,4)fsm = Fsm()#force power supply in PWM mode (fixes false inputs from rotary caused by power supply noise)Pin(23, Pin.IN).high()action = 0def rotary_changed(change):    global action    action = change      rotary.add_handler(rotary_changed)while True:    fsm.update(action)    action = 0    time.sleep(0.1)            